import * as React from 'react'
import { NextPage } from 'next'

const css = require('./index.module.css')

const AdminPage: NextPage = (): JSX.Element => {
    const [isSignedIn, setIsSignedIn] = React.useState<boolean>(false)

    const signInHandler = (email: string, password: string) =>  {

    }

    return (
        <div className={css.Main}>
            { !isSignedIn && <AuthForm signIn={signInHandler} /> }
        </div>
    )
}

interface IAuthForm {
    signIn: (email: string, password: string) => void
}

interface IFormElement {
    elementType: string
    inputType: string
    placeholder?: string
    require?: boolean
    value?: string
}

interface IFormState {
    [email: string]: IFormElement
    password: IFormElement
    submit: IFormElement
}

const initialAuthFormState: IFormState = {
    email: {
        elementType: 'input',
        inputType: 'email',
        placeholder: 'email',
        require: true,
        value: '',
    },
    password: {
        elementType: 'input',
        inputType: 'password',
        placeholder: 'password',
        require: true,
        value: '',
    },
    submit: {
        elementType: 'input',
        inputType: 'submit',
        value: 'Sign In'
    },
}
   


const AuthForm: React.FunctionComponent<IAuthForm> = ({ signIn }): JSX.Element => {
    const [formState, setFormState] = React.useState<IFormState>(initialAuthFormState)

    return (
        <form className={css.AuthFormContainer}>
            { Object.keys(formState).map((elementKey: string) => {
                const element: IFormElement = formState[elementKey]
                return (
                    <p> {element.elementType} </p>
                )
            })}
        </form>
    )
}

export default AdminPage
