    return (
        <div className={css.Main}>
            { !isSignedIn && <AuthForm signIn={signInHandler} /> }
        </div>
    )
}

interface IAuthForm {
    signIn: (email: string, password: string) => void
}

interface IFormElement {
    elementType: string
    inputType: string
    placeholder?: string
    require?: boolean
    value?: string
    className?: string
    icon? : { focus?: any, unfocus?: any }
}

interface IFormState {
    [email: string]: IFormElement
    password: IFormElement
    submit: IFormElement
}

const initialAuthFormState: IFormState = {
    email: {
        elementType: 'input',
        inputType: 'email',
        placeholder: 'email',
        require: true,
        value: '',
        className: css.EmailInput,
        icon: { focus: faEnvelopeOpen, unfocus: faEnvelope }
    },
    password: {
        elementType: 'input',
        inputType: 'password',
        placeholder: 'password',
        require: true,
        value: '',
        className: css.PasswordInput,
        icon: { focus: faUnlock, unfocus: faLock }
    },
    submit: {
        elementType: 'input',
        inputType: 'submit',
        value: 'Sign In',
        className: css.SubmitButton
    },
}
   


