import * as React from 'react'
import { NextPage } from 'next'

const css = require('./index.module.css')

const AdminPage: NextPage = (): JSX.Element => {
    const [isSignedIn, setIsSignedIn] = React.useState<boolean>(false)

    const signInHandler = (email: string, password: string) =>  {

    }

    return (
        <div className={css.Main}>
            { !isSignedIn && <AuthForm signIn={signInHandler} /> }
        </div>
    )
}

interface IAuthForm {
    signIn: (email: string, password: string) => void
}

interface IFormElement {
    elementType: string
    inputType: string
    placeholder?: string
    require?: boolean
    value?: string
    className?: string
}

interface IFormState {
    [email: string]: IFormElement
    password: IFormElement
    submit: IFormElement
}

const initialAuthFormState: IFormState = {
    email: {
        elementType: 'input',
        inputType: 'email',
        placeholder: 'email',
        require: true,
        value: '',
        className: css.EmailInput
    },
    password: {
        elementType: 'input',
        inputType: 'password',
        placeholder: 'password',
        require: true,
        value: '',
        className: css.PasswordInput
    },
    submit: {
        elementType: 'input',
        inputType: 'submit',
        value: 'Sign In',
        className: css.SubmitButton
    },
}
   


const AuthForm: React.FunctionComponent<IAuthForm> = ({ signIn }): JSX.Element => {
    const [formState, setFormState] = React.useState<IFormState>(initialAuthFormState)

    const changeHandler = (element: string, event: React.KeyboardEvent) => {
        
    }

    return (
        <div className={css.AuthFormContainer}>
            <h2 className={css.FormTitle}>Sign In</h2>
            <form className={css.AuthForm}>
                { Object.keys(formState).map((elementKey: string) => {
                    const element: IFormElement = formState[elementKey]
                    return (
                        <input {...element} /> 
                    )
                })}
            </form>
        </div>
    )
}

export default AdminPage
